// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BasepayProcessor
 * @notice Handles USDC payments with automatic 1% fee to BasePay treasury
 * @dev Non-custodial payment processor using EIP-2612 permit (1 signature only)
 */
contract BasepayProcessor is Ownable, ReentrancyGuard {
    
    /// @notice USDC token with permit support
    IERC20Permit public immutable usdc;

    /// @notice BasePay treasury wallet (receives fee)
    address public constant TREASURY_WALLET =
    address(0xDcaf4cBAc0246DE4e1001444B02cBE814E4bAfa4);


    /// @notice Fee: 1% (100 basis points)
    uint256 public constant FEE_PERCENTAGE = 100;
    uint256 public constant BASIS_POINTS = 10_000;

    /// @notice Emitted after a successful payment
    event PaymentProcessed(
        address indexed sender,
        address indexed recipient,
        uint256 totalAmount,
        uint256 recipientReceived,
        uint256 feeAmount,
        uint256 timestamp
    );

    /**
     * @notice Constructor
     * @param _usdc Address of USDC token on Base
     * Base Mainnet USDC:
     * 0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913
     */
    constructor(address _usdc) Ownable(msg.sender) {
        require(_usdc != address(0), "Invalid USDC address");
        usdc = IERC20Permit(_usdc);
    }

    /**
     * @notice Pay USDC using permit (EIP-2612)
     * @dev User signs once, no approve tx needed
     * @param recipient Address receiving payment
     * @param totalAmount Total amount user sends (100%)
     * @param deadline Permit deadline
     * @param v Signature param
     * @param r Signature param
     * @param s Signature param
     */
    function payWithPermit(
        address recipient,
        uint256 totalAmount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(totalAmount > 0, "Amount must be > 0");

        // Calculate fee and net amount
        uint256 feeAmount = (totalAmount * FEE_PERCENTAGE) / BASIS_POINTS;
        uint256 recipientAmount = totalAmount - feeAmount;

        require(recipientAmount > 0, "Amount too small");

        // Permit approval (EIP-2612)
        usdc.permit(
            msg.sender,
            address(this),
            totalAmount,
            deadline,
            v,
            r,
            s
        );

        // Transfer to recipient (99%)
        require(
            IERC20(address(usdc)).transferFrom(
                msg.sender,
                recipient,
                recipientAmount
            ),
            "Transfer to recipient failed"
        );

        // Transfer fee to treasury (1%)
        if (feeAmount > 0) {
            require(
                IERC20(address(usdc)).transferFrom(
                    msg.sender,
                    TREASURY_WALLET,
                    feeAmount
                ),
                "Transfer to treasury failed"
            );
        }

        emit PaymentProcessed(
            msg.sender,
            recipient,
            totalAmount,
            recipientAmount,
            feeAmount,
            block.timestamp
        );
    }

    /**
     * @notice Calculate fee split for an amount
     * @param amount Total amount
     */
    function calculateFee(uint256 amount)
        external
        pure
        returns (uint256 feeAmount, uint256 recipientAmount)
    {
        feeAmount = (amount * FEE_PERCENTAGE) / BASIS_POINTS;
        recipientAmount = amount - feeAmount;
    }

    /**
     * @notice Get contract configuration
     */
    function getConfig()
        external
        pure
        returns (address treasury, uint256 feePercentage)
    {
        return (TREASURY_WALLET, FEE_PERCENTAGE);
    }
}

